package com.example.navigation_test.dataimport com.example.navigation_test.domain.models.Todoclass TodoListMapper {    fun mapEntityToDbModel(todoItem: Todo) = TodoItemDbModel(        id = todoItem.id,        title = todoItem.title,        description = todoItem.description,        completed = todoItem.completed    )    fun mapDbModelToEntity(todoItemDbModel: TodoItemDbModel) = Todo(        id = todoItemDbModel.id,        title = todoItemDbModel.title,        description = todoItemDbModel.description,        completed = todoItemDbModel.completed    )    fun mapListDbModelToListEntity(list: List<TodoItemDbModel>) = list.map {        mapDbModelToEntity(it)    }}