package com.example.navigation_test.presentation.navigationimport androidx.compose.runtime.Composableimport androidx.navigation.NavHostControllerimport androidx.navigation.NavTypeimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.navArgumentimport com.example.navigation_test.domain.models.Todoimport com.google.gson.Gson@Composablefun AppNavGraph(    navHostController: NavHostController,    todoListScreenContent: @Composable () -> Unit,    addTodoScreenContent: @Composable () -> Unit,    editTodoScreenContent:@Composable (todoItem:Todo) -> Unit,) {    NavHost(        navController = navHostController,        startDestination = ScreenRouts.ROUT_TODOLIST    ) {        composable(ScreenRouts.ROUT_TODOLIST) {            todoListScreenContent()        }        composable(ScreenRouts.ROUT_ADD_TODO) {            addTodoScreenContent()        }        composable(            route = ScreenRouts.EDIT_TODO_SCREEN,            arguments = listOf(                navArgument(ScreenRouts.KEY_TODO_ITEM){                    type = NavType.StringType                }            )        ) {            val todoItemJson = it.arguments?.getString(ScreenRouts.KEY_TODO_ITEM) ?: ""            val todoItem = Gson().fromJson(todoItemJson,Todo::class.java)            editTodoScreenContent(todoItem)        }    }}