package com.example.navigation_test.presentation.navigationimport android.net.Uriimport com.example.navigation_test.domain.models.Todoimport com.google.gson.Gsonsealed class ScreenRouts(    val rout: String) {    object TotoList : ScreenRouts(ROUT_TODOLIST)    object AddTodoScreen : ScreenRouts(ROUT_ADD_TODO)    object EditTodoScreen : ScreenRouts(EDIT_TODO_SCREEN) {        private const val ROUT_FOR_ARGS = "edit_todo_screen"        fun getRoutWithArgs(todo: Todo): String {            val todoItemJson = Gson().toJson(todo)            return "$ROUT_FOR_ARGS/${todoItemJson.encode()}"        }    }    companion object {        const val KEY_TODO_ITEM = "todo_item"        const val ROUT_TODOLIST = "rout_todolist"        const val ROUT_ADD_TODO = "add_todo"        const val EDIT_TODO_SCREEN = "edit_todo_screen/{$KEY_TODO_ITEM}"        const val ADD_TODO_SCREEN_MODE = "add_todo_screen_mode"        const val EDIT_TODO_SCREEN_MODE = "edit_todo_screen_mode"    }}fun String.encode(): String {    return Uri.encode(this)}